insert  into `balances`(`numbalan`,`nombalan`,`Descripcion`,`Aparece`,`perdidas`,`Predeterminado`) values (51,'RATIO TESORERIA','Expresa la capacidad que tiene la empresa para, con el disponible y las deudas a su favor, poder hacer frente a las deudas a corto plazo. \r\nSe calcula dividendo el Disponible más el Realizable cierto, entre el Exigible a corto plazo.\r\n\r\nSi el resultado oscila entre el 1 y el 1,2 significa que la empresa puede hacer frente a la totalidad de sus deudas a corto plazo.\r\n \r\n\r\n                   Activo realizable a C.P.  + Cajas y Bancos\r\nRATIO=   ------------------------------------------------------------\r\n                                     Deudas exigibles a C.P\r\n',0,0,0);
insert  into `balances`(`numbalan`,`nombalan`,`Descripcion`,`Aparece`,`perdidas`,`Predeterminado`) values (52,'RATIO DE LIQUIDEZ','Ratio utilizado para medir la capacidad de la empresa para hacer frente a sus deudas a corto plazo realizando su activo circulante.\r\nSe calcula averiguando la relación existente entre el total del activo circulante sobre el total de las deudas a corto plazo.\r\nSi el resultado se aproxima a 0,1 indicaría que la empresa puede hacer  frente al 10% de sus deudas a corto plazo.\r\n- Un valor menor indicaría dificultad para hacer frente a los pagos.\r\n- Un valor mayor indicaría exceso de disponibilidad.\r\n\r\n                     Total activo circulante\r\nRATIO  =   -----------------------------------------\r\n                      Deudas a corto plazo\r\n\r\n',0,0,0);
insert  into `balances`(`numbalan`,`nombalan`,`Descripcion`,`Aparece`,`perdidas`,`Predeterminado`) values (53,'RATIO DE SOLVENCIA','Ratio que mide la capacidad de una empresa para hacer frente sus obligaciones de pago.\r\nEste ratio lo podemos encontrar tanto de forma agregada, activo total entre pasivo total, o de forma desagregada esto es en función del activo y pasivo corriente o no corriente.\r\nObviamente este es uno de los ratios que las entidades financieras tienen en consideración a la hora de analizar las operaciones de financiación que solicitan sus clientes.\r\nEl resultado debe estar cercano a 1,6 y nunca por debajo de 1. Si fuera inferior la empresa podría entrar en concurso de acreedores.\r\n\r\n                  Activo no corriente + Activo corriente\r\nRATIO= ---------------------------------------------------------\r\n                  Pasivo no corriente + Pasivo corriente\r\n',0,0,0);
