insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('189','GARANTIAS FINANCIERAS A LARGO PLAZO','N',0,'GARANTIAS FINANCIERAS A LARGO PLAZO',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Garantías financieras concedidas por la empresa a plazo superior a un año. En particular, avales otorgados, siempre y cuando no proceda su registro en el subgrupo 14. \r\n\r\nSu movimiento es el siguiente: \r\n\r\na) Se abonará: \r\n    a1) A la constitución, por el valor razonable del pasivo financiero, con cargo a cuentas del subgrupo 57. \r\n    a2) Por el gasto financiero devengado, con cargo, generalmente, a la cuenta 662.\r\n    a2) Por el aumento de la obligación, con cargo a la cuenta 669. \r\n\r\nb) Se cargará: \r\n    b1) Por la disminución de la obligación y por los ingresos devengados, con abono a la cuenta 769. \r\n    b2) A la cancelación anticipada, con abono a cuentas del subgrupo 57. \r\n',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
