insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('20','INMOVILIZACIONES INTANGIBLES','N',0,'INMOVILIZACIONES INTANGIBLES',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Las inmovilizaciones intangibles son activos no monetarios sin apariencia física susceptibles de valoración económica, así como los anticipos a cuenta entregados a proveedores de inmovilizado.\r\n\r\nAdemás de los elementos intangibles mencionados, existen otros elementos de esta naturaleza que serán reconocidos como tales en balance, siempre y cuando cumplan las condiciones señaladas en el Marco Conceptual de la Contabilidad, así como los requisitos especificados en las normas de registro y valoración. Entre otros, los siguientes: derechos comerciales, propiedad intelectual o licencias, Para su registro se abrirá una cuenta en este subgrupo cuyo movimiento será similar al descrito a continuación para las restantes cuentas del inmovilizado intangible. \r\n\r\nLas cuentas de este subgrupo figurarán en el activo no corriente del balance\r\n',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,0);
