insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('203','PROPIEDAD INDUSTRIAL','N',0,'PROPIEDAD INDUSTRIAL',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Importe satisfecho por la propiedad o por el derecho al uso o a la concesión del uso de las distintas manifestaciones de la propiedad industrial, en los casos en que, por las estipulaciones del contrato, deban inventariarse por la empresa adquirente. Este concepto incluye, entre otras, las patentes de invención, los certificados de protección de modelos de utilidad pÃºblica y las patentes de introducción. \r\n\r\nEsta cuenta comprenderá también los gastos realizados en desarrollo cuando los resultados de los respectivos proyectos emprendidos por la empresa fuesen positivos y, cumpliendo los necesarios requisitos legales, se inscriban en el correspondiente Registro. \r\n\r\nSu movimiento es el siguiente: \r\n\r\na) Se cargará: \r\n    a1) Por la adquisición a otras empresas, con abono, generalmente, a cuentas del subgrupo 57. \r\n    a2) Por ser positivos e inscritos en el correspondiente Registro PÃºblico, los resultados de desarrollo, con                  abono a la cuenta 201.\r\n    a3) Por los desembolsos exigidos para la inscripción en el correspondiente Registro, con abono,                     generalmente, a cuentas del subgrupo 57. \r\n \r\nb) Se abonará por las enajenaciones y en general por la baja del activo, con cargo, generalmente, a cuentas     del subgrupo 57 y en caso de pérdidas a la cuenta 670. \r\n\r\n',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
