insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('24','INVERSIONES FINANCIERAS EN PARTES VINCULADAS','N',0,'INVERSIONES FINANCIERAS EN PARTES VINCULADAS',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Inversiones financieras a largo plazo en empresas del grupo, multigrupo, asociadas y otras partes vinculadas, cualquiera que sea su forma de instrumentación, incluidos los intereses devengados, con vencimiento superior a un año o sin vencimiento (como los instrumentos de patrimonio), cuando la empresa no tenga la inteción de venderlos en el corto plazo. También se incluirán en este subgrupo las fianzas y depósitos a largo plazo constituidas con estas personas o entidades. Estas inversiones se recogerán en las cuentas de tres o más cifras que se desarrollen.\r\n\r\nEn caso de que los valores representativos de deuda o los créditos devenguen intereses explícitos con vencimiento superior a un año, se crearán las cuentas necesarias para identificarlos, debiendo figurar en el balance en la misma partida que se incluya el activo que los genera.\r\n\r\nLa parte de las inversiones a largo plazo, con personas o entidades vinculadas, que tenga vencimiento a corto deberá figurar en el activo corriente del balance, en el epígrafe \"Inversiones en empresas del grupo y asociadas a corto plazo\"; a estos efectos se traspasará el importe que represente la inversión a largo plazo con vencimiento a corto plazo, incluidos en su caso los intereses devengados, a las cuentas correspondientes del subgrupo 53. \r\n',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('240','PARTICIPACIONES A LARGO PLAZO EN PARTES VINCULADAS','N',0,'PARTICIPACIONES A LARGO PLAZO EN PARTES VINCULADAS',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Inversiones a largo plazo en derechos sobre el patrimonio neto -con o sin cotización en un mercado regulado- de partes vinculadas, generalmente, acciones emitidas por una sociedad anónima o participaciones en sociedades de responsabilidad limitada. \r\n\r\nFigurará en el activo no corriente del balance\r\n\r\n',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('2403','PARTICIPACIONES A LARGO PLAZO EN EMPRESAS DEL GRUPO','N',0,'PARTICIPACIONES A LARGO PLAZO EN EMPRESAS DEL GRUPO',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'El movimiento es el siguiente: \r\n\r\na) Se cargarán:\r\n    a1) A la suscripción o compra, con abono, generalmente, a cuentas del subgrupo 57 y, en su caso,     a la           cuenta 249. \r\n    a2) En su caso, en el momento en que el importe recuperable sea superior al valor contable de las           inversiones, hasta el limite de los ajustes valorativos negativos previos imputados directamente a           patrimonio neto, con abono a las cuentas 991 ó 992.\r\n\r\nb) Se abonarán:\r\n    b1) En su caso, por el importe del deterioro estimado, hasta el límite de los ajustes valorativos positivos           previos imputados directamente a patrimonio neto, con cargo a las cuentas 891 u 892.    \r\n    b2) Por las enajenaciones y en general por su baja del activo, con cargo, generalmente, a cuentas del                   subgrupo 57, si existen desembolsos pendientes a la cuenta 248 y en caso de pérdidas a la cuenta 673. \r\n',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
