insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('241','VALORES REPRESENTATIVOS  DEUDA A L. PLAZO A PARTES VINCULA','N',0,'VALORES REPRESENTATIVOS  DEUDA A L. PLAZO A PARTES VINCULA',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Inversiones a largo plazo en obligaciones, bonos u otros valores representativos de deuda, incluidos aquéllos que fijan su rendimiento en función de índices o sistemas análogos, emitidos por partes vinculadas, con vencimiento superior a un año. \r\n\r\nFigurará en el activo no corriente del balance. \r\n\r\n',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('2413','VALORES REPRESENTATIVOS DEUDA LARGO PLAZO, EMP. GRUPO','N',0,'VALORES REPRESENTATIVOS DEUDA LARGO PLAZO, EMP. GRUPO',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'El movimiento es el siguiente: \r\n\r\na) Se cargarán:\r\n    a1)  A la suscripción o compra, por el precio de adquisición, excluidos los intereses explícitos devengados y           no vencidos, con abono, a cuentas del subgrupo 57. \r\n    a2) Por el ingreso financiero devengado hasta alcanzar el valor de reembolso del valor, con abono,           generalmente, a la cuenta 761.\r\n\r\nb) Se abonará por las enajenaciones, amortizaciones anticipadas o baja del activo de los valores, con cargo,     a cuentas del subgrupo 57 y en caso de pérdidas a la cuenta 666. \r\n\r\nc) Si los valores se clasifican en la categoría de \"Activos financieros disponibles para la venta\" se cargarán o abonarán, por las variaciones en su valor razonable, con abono o cargo, respectivamente, a las cuentas 900 y 800, salvo en la parte correspondiente a difrencias de cambio que se registrará con abono o cargo a las cuentas 768 y 668. También se cargarán cuando se produzca el deterioro del valor por el saldo negativo acumulado en el patrimonio neto, con abono a la cuenta 902.\r\n',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
