insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('251','VALORES REPRESENTATIVOS DE DEUDA A LARGO PLAZO','N',0,'VALORES REPRESENTATIVOS DE DEUDA A LARGO PLAZO',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Inversiones a largo plazo en obligaciones, bonos u otros valores representativos de deuda, incluidos aquellos que fijan su rendimiento en función de índices o sistemas análogos. \r\n\r\nCuando los valores suscritos o adquiridos hayan sido emitidos por partes vinculadas, la inversión se reflejará en la cuenta 241. \r\n\r\nFigurará en el activo no corriente del balance. \r\n\r\nCon carácter general, su movimiento es el siguiente: \r\n\r\na) Se cargará:\r\n    a1) A la suscripción o compra, por el precio de adquisición, excluidos los intereses explícitos devengados y           no vencidos, con abono, a cuentas del subgrupo 57. \r\n    a2) Por el ingreso financiero devengado hasta alcanzar el valor de reembolso del valor, con abono,           generalmente, a la cuenta 761.\r\n\r\nb) Se abonará por las enajenaciones, amortizaciones anticipadas o baja del activo de los valores, con cargo,     a cuentas del subgrupo 57 y en caso de pérdidas a la cuenta 666. \r\n\r\nc) Si los valores se clasifican en la categoría de \"Activos financieros disponibles para la venta\"  se cargará o abonará, por las variaciones en su valor razonable, con abono o  cargo, respectivamente, a las cuentas 900 y 800, salvo la parte que correspondiente a diferencias de cambio que se registrará con abono o cargo a las cuentas 768 y 668. También se cargará cuando se produzca el deterioro del valor por el saldo negativo acumulado en el patrimonio neto, con abono a la cuenta 902.\r\n',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('252','CREDITOS A LARGO PLAZO','N',0,'CREDITOS A LARGO PLAZO',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Los préstamos y otros créditos no comerciales concedidos a terceros, incluidos los formalizados mediante efectos de giro, con vencimiento superior a un año. \r\n\r\nCuando los créditos hayan sido concertados con partes vinculadas, la inversión se reflejará en la cuenta 242. \r\n\r\nFigurará en el activo no corriente del balance. \r\n\r\nSu movimiento es el siguiente: \r\n\r\na) Se cargará:\r\n    a1) A la formalización del crédito, por el importe de éste, con abono, generalmente, a cuentas del               subgrupo 57. \r\n    a2) Por el ingreso financiero devengado hasta alcanzar el valor de reembolso del crédito, con abono,           generalmente, a la cuenta 762.\r\n\r\nb) Se abonará por el reintegro anticipado, total o parcial o baja del activo, con cargo, generalmente, a cuentas     del subgrupo 57 y en caso de pérdidas a la cuenta 667. \r\n',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('253','CREDITOS A LARGO PLAZO POR ENAJENACION DEL INMOVILIZADO','N',0,'CREDITOS A LARGO PLAZO POR ENAJENACION DEL INMOVILIZADO',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Créditos a terceros cuyo vencimiento sea superior a un año, con origen en operaciones de enajenación de inmovilizado. \r\n\r\nCuando los créditos por enajenación de inmovilizado hayan sido concertados con partes vinculadas, la inversión se reflejará en la cuenta 242. \r\n\r\nFigurará en el activo no corriente del balance. \r\n\r\nSu movimiento es el siguiente: \r\n\r\na) Se cargará:\r\n    a1) Por el importe de dichos créditos, excluidos los intereses que en su caso se hubieran acordado, con           abono a cuentas del grupo 2. \r\n    a2) Por el ingreso financiero devengado hasta alcanzar el valor de reembolso del crédito, con abono,          generalmente, a la cuenta 762.\r\n\r\nb) Se abonará a la cancelación anticipada, total o parcial o baja del activo, con cargo, generalmente, a         cuentas del subgrupo 57 y en caso de pérdidas a la cuenta 667. \r\n',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('254','CREDITOS A LARGO PLAZO AL PERSONAL','N',0,'CREDITOS A LARGO PLAZO AL PERSONAL',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Créditos concedidos al personal de la empresa, que no tenga la calificación de parte vinculada, cuyo vencimiento sea superior a un año. \r\n\r\nFigurará en el activo no corriente del balance. \r\n\r\nSu movimiento es el siguiente: \r\n\r\na) Se cargará:\r\n    a1) A la formalización del crédito, por el importe de éste, con abono, generalmente, a cuentas del               subgrupo 57. \r\n    a2) Por el ingreso financiero devengado hasta alcanzar el valor de reembolso del crédito, con abono,           generalmente, a la cuenta 762.\r\n\r\nb) Se abonará por el reintegro anticipado, total o parcial o baja del activo, con cargo, generalmente, a cuentas     del subgrupo 57 y en caso de pérdidas a la cuenta 667. \r\n\r\n\r\n',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('255','ACTIVOS POR DERIVADOS FINANCIEROS A LARGO PLAZO','N',0,'ACTIVOS POR DERIVADOS FINANCIEROS A LARGO PLAZO',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Importe correspondiente a las operaciones con derivados financieros con valoración favorable para la empresa cuyo plazo de liquidación sea superior a un año. También se incluyen los derivados implícitos de instrumentos financieros hibridos adquiridos, emitidos o asumidos, que cumplan los criterios para su inclusión en esta cuenta, creándose en caso necesario cuentas de cuatro o más cifras que identifiquen que se trata de un derivado implícito.\r\n\r\nEn particular, se recogerán en esta cuenta las primas pagadas en operaciones con opciones, así como, con carácter general, las variaciones en el valor razonable de los activos por derivados financieros con los que opere la empresa: opciones, futuros, permutas financieras, derivados de crédito, etc, cuyo vencimiento o fecha de liquidación sea superior a un año.\r\n\r\nFigurará en el activo no corriente del balance.\r\n\r\n',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('2550','ACTIVOS POR DERIVADOS FINANCIEROS LARGO PLAZO, CARTERA NEG.','N',0,'ACTIVOS POR DERIVADOS FINANCIEROS LARGO PLAZO, CARTERA NEG.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'El movimiento de la cuenta es el siguiente:\r\n\r\na) Se cargará:\r\n    a1) Por las cantidades satisfechas en el momento de la contratación, con abono, generalmente, a                         cuentas del subgrupo 57.\r\n    b2) Por las ganancias que se generen en el ejercicio con abono a la cuenta 7630.\r\n\r\nb) Se abonará:\r\n    b1) Por las pérdidas que se generen en el ejercicio hasta el límite del importe por el que figurará registrado el           derivado en el activo en el ejercicio anterior, con cargo a la cuenta 6630.\r\n    b2) Por el importe recibido en el momento de la liquidación, con cargo, generalmente, a cuentas del               subgrupo 57.\r\n\r\n',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('2553','ACTIVOS POR DERIVADOS FINANCIEROS LARGO PLAZO, INST. COBERTU','N',0,'ACTIVOS POR DERIVADOS FINANCIEROS LARGO PLAZO, INST. COBERTU',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Su movimiento es el siguiente:\r\n\r\na) Se cargará por las cantidades satisfechas en el momento de la contratación, con abono, generalmente a cuentas del subgrupo 57.\r\n\r\nb) Cuando el derivado se utilice como instrumento de cobertura, en una cobertura de valor razonable.\r\n    b1) Se cargará por las ganancias que se generen en el ejercicio al palicar las reglas que rigen la           contabilidad de coberturas, con abono a una cuenta que se imputará a la cuenta de pérdidas y           ganancias en la misma partida en la que se incluyan las ganancias que se generen en las partidas           cubiertas al valorar el riesgo cubierto del valor razonable.\r\n    b2) Se abonará:\r\n       i) Por las pérdidas que se generen en el ejercicio al aplicar las reglas que rigen la contabilidad de           coberturas, hasta el límite del importe por el que figurara registrado el derivado del activo en el activo en           el ejercicio anterior, con cargo a una cuenta que se imputará en la cuenta de pérdidas y ganancias en           la misma partida en la que se incluyan las ganancias que se generen en las partidas cubiertas al valorar           el riesgo cubierto por su valor razonable.\r\n       ii) En el momento en que se adquiera el activo o asuma el pasivo cubiertos, con cargo a las cuentas en             que se contabilicen dichos elementos patrimoniales.\r\n \r\nc) Cuando el derivado se utilice como instrumento de cobertura, en otras operaciones de cobertura, por la parte eficaz, se cargará o abonará, por la ganancia o pérdida que se genere en el ejercicio al aplicar las reglas que rigen la contabilidad de coberturas, con abono o cargo, a las cuentas del subgrupo 91 y 81, respectivamente, y por la parte ineficaz, a las cuentas 7633 y 6633.\r\n\r\nd) Se abonará por el importe recibido en el momento de la liquidación, con cargo generalmente a cuentas del subgrupo 57.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('257','DERECHOS REEMBOLSO DE CONTRATOS SEGURO RETRIB. LARGO PLAZO','N',0,'DERECHOS REEMBOLSO DE CONTRATOS SEGURO RETRIB. LARGO PLAZO',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Derechos de reembolso exigibles a una entidad aseguradora, que no cumpliendo los requisitos para ser calificados como activos efectos de acuerdo con lo dispuesto en las normas de registro y valoración deba reconocerse en el activo del balance.\r\n\r\nSu movimiento es el siguiente:\r\n\r\na) Se cargará:\r\n    a1) Por las cantidades satisfechas en el concepto de primas, con abono, generalmente, a cuentas del           subgrupo 57.\r\n    a2) Por el reconocimiento de ganancias actuariales, con abono a la cuenta 950, en caso de tratarse de           retribuciones post-empleo, debiendo abonarse a una cuenta del subgrupo 64 en las restantes           retribuciones a largo plazo al personal.\r\n    a2) Por el rendimiento esperado de los derechos de reembolso, con abono a la cuenta 767.\r\n   \r\nb) Se abonará:\r\n    b1) Por la disposición que se realice del derecho de reembolso, con cargo a la cuenta 140, o con cargo           a cuentas del subgrupo 57.\r\n    b2) Por el reconocimiento de pérdidas actuariales, con cargo a la cuenta 850, en caso de tratarse de           retribuciones post-empleo, debiendo cargarse a una cuenta del subgrupo 64 en las restantes           retribuciones a largo plazo del personal.\r\n    b3) Por el exceso de valor del derecho de reembolso que suponga un reembolso directo, con cargo a           cuentas del subgrupo 57.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('258','IMPOSICIONES A LARGO PLAZO','N',0,'IMPOSICIONES A LARGO PLAZO',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Saldos favorables en Bancos e Instituciones de Crédito formalizados por medio de \"cuenta de plazo\" o similares, con vencimiento superior a un año y de acuerdo con las condiciones que rigen para el sistema financiero. \r\n\r\nCuando las imposiciones a plazo hayan sido concertadas con entidades de crédito vinculadas, la inversión se reflejará en la cuenta 242. \r\n\r\nFigurará en el activo no corriente del balance. \r\n\r\nSu movimiento es el siguiente: \r\n\r\na) Se cargará a la formalización, por el importe entregado. \r\n\r\nb) Se abonará a la recuperación o traspaso anticipado de los fondos. \r\n',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('259','DESEMBOLSOS PDTES. SOBRE PARTICIP. EN EL PATRIMONIO L.PLAZO','N',0,'DESEMBOLSOS PDTES. SOBRE PARTICIP. EN EL PATRIMONIO L.PLAZO',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Desembolsos pendientes, no exigidos, sobre instrumentos de patrimonio de entidades que no tengan la consideración de partes vinculadas. \r\n\r\nFigurará en el activo no corriente del balance, minorando la partida en que se contabilicen las correspondientes participaciones. \r\n\r\nSu movimiento es el siguiente: \r\n\r\na) Se abonará a la adquisición o suscripción de los instrumentos de patrimonio, por el importe pendiente de     desembolsar, con cargo a la cuenta 250. \r\n\r\nb) Se cargará por los desembolsos que se vayan exigiendo, con abono a la cuenta 556 o a la cuenta 250 por     los saldos pendientes cuando se enajenen instrumentos de patrimonio no desembolsados totalmente. \r\n',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
