insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('490','DETERIORO DEL VALOR DE CREDITOS POR OPERACIONES COMERCIALES','N',0,'DETERIORO DEL VALOR DE CREDITOS POR OPERACIONES COMERCIALES',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Importe de las correcciones valorativas por deterioro de créditos incobrables, con origen en operaciones de tráfico. \r\n\r\nSu movimiento es el siguiente, segÃºn la alternativa adoptada por la empresa: \r\n\r\n1. Cuando la empresa cifre el importe del deterioro al final del ejercicio mediante una estimación global del riesgo de fallidos existentes en los saldos de clientes y deudores, siempre y cuando su importe, individualmente considerados, no sea significativo: \r\n\r\na) Se abonará, al final del ejercicio, por la estimación realizada, con cargo a la cuenta 694. \r\n\r\nb) Se cargará, igualmente al final del ejercicio, por la corrección realizada al cierre del ejercicio precedente, con abono a la cuenta 794. \r\n\r\n2. Cuando la empresa cifre el importe del deterioro mediante un sistema individualizado de seguimiento de saldos de clientes y deudores: \r\n\r\na) Se abonará, a lo largo del ejercicio, por el importe de la pérdida que se vaya estimando, con cargo a la cuenta 694. \r\n\r\nb) Se cargará a medida que se vayan dando de baja los saldos de clientes y deudores para los que se dotó la cuenta correctora de forma individualizada o cuando la pérdida estimada disminuya como consecuencia de un evento posterior, con abonoa la cuenta 794.\r\n',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
