insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('500','OBLIGACIONES Y BONOS A CORTO PLAZO','N',0,'OBLIGACIONES Y BONOS A CORTO PLAZO',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Obligaciones y bonos en circulación no convertibles en acciones cuyo vencimiento vaya a producirse en un plazo no superior a un año. \r\n\r\nCon carácter general, su movimiento es el siguiente: \r\n\r\na) Se abonará: \r\n    a1) En el momento de la emisión, por el importe recibido, minorado en los gastos de la transacción, con           cargo a cuentas del subgrupo 57. \r\n    a2) Por el gasto financiero devengado hasta el valor de reembolso de la deuda, con cargo, generalmente, a           la cuenta 661.\r\n\r\nb) Se cargará por el importe a reembolsar de los valores, a la amortización de los mismos, con abono a la cuenta 509\r\n',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
