insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('502','ACCIONES O PARTICIPACIONES A CORTO PLAZO CONTAB. COMO PASIVO','N',0,'ACCIONES O PARTICIPACIONES A CORTO PLAZO CONTAB. COMO PASIVO',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Capital social escriturado y, en su caso, prima de emisión o asunción en las sociedads que revistan forma mercantil que, atendiendo a las características de la emisión, deba contabilizarse como pasivo financiero y cuyo rescate se prevea a corto plazo. \r\n\r\nEn particular, determinadas acciones rescatables y acciones o participaciones sin voto. \r\n\r\nSu movimiento es el siguiente: \r\n\r\na) Se abonará por el capital inicial y las sucesivas ampliaciones, en el momento de su inscripción en el         Registro Mercantil, con cargo a la cuenta 199. \r\n\r\nb) Se cargará por la cancelación o reducciones del mismo y a la extinción de la sociedad, una vez transcurrido     el período de liquidación.\r\n',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
