insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('530','PARTICIPACIONES A CORTO PLAZO EN PARTES VINCULADAS','N',0,'PARTICIPACIONES A CORTO PLAZO EN PARTES VINCULADAS',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Inversiones a corto plazo en derechos sobre el patrimonio neto -con o sin cotización en un mercado regulado- de partes vinculadas; generalmente, acciones emitidas por una sociedad anónima o participaciones en sociedades de responsabilidad limitada. \r\n\r\nFigurará en el activo corriente del balance.\r\n\r\n',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('5303','PARTICIPACIONES A CORTO PLAZO EN EMPRESAS DEL GRUPO','N',0,'PARTICIPACIONES A CORTO PLAZO EN EMPRESAS DEL GRUPO',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'El movimiento de las cuentas es el siguiente: \r\n\r\na) Se cargará:\r\n   a1) A la suscripción o compra, con abono, generalmente, a cuentas del subgrupo 57 y, en su caso, a la          cuenta 539. \r\n   a2) En su caso, en el momento en que el importe recuperable sea superior al valor contable de las          inversiones, hasta el límite de los ajustes valorativos negativos previos imputados directamente al          patrimonio neto, con abono a las cuentas 991 ó 992.\r\n\r\nb) Se abonará:\r\n    b1) En su caso, por el importe del deterioro estimado, hasta el límite de los ajustes valorativos positivos            previos imputados directamente a patrimonio neto, con cargo a las cuentas 891 u 892.\r\n    b2) Por las enajenaciones y en general por su baja del activo, con cargo, generalmente, a cuentas del           subgrupo 57, si existen desembolsos pendientes a la cuenta 538 y en caso de pérdidas a la cuenta 666\r\n',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
