insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('546','INTERESES A CORTO PLAZO DE VALORES REPRESENTATIVOS DE DEUDA','N',0,'INTERESES A CORTO PLAZO DE VALORES REPRESENTATIVOS DE DEUDA',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Intereses a cobrar, con vencimiento no superior a un año, de valores representativos de deuda y de crédito. \r\n\r\nFigurará en el activo corriente del balance. \r\n\r\nSu movimiento es el siguiente: \r\n\r\na) Se cargará: \r\n    a1) A la suscripción o compra de los valores, por el importe de los intereses explícitos corridos y no           vencidos, cuyo vencimiento no sea superior a un año, con abono, generalmente, a cuentas del           subgrupo 57. \r\n   a2) Por los intereses explícito devengados, cuyo vencimiento no sea superior a un año, con abono a la          cuenta 761.\r\n\r\nb) Se abonará:\r\n    b1) Por el importe de los intereses cobrados, con cargo a cuentas del subgrupo 57. \r\n    b2) A la enajenación, amortización o baja del activo de los valores, con cargo, generalmente, a cuentas del           subgrupo 57 y en caso de pérdidas a la cuenta 666.\r\n\r\n',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('547','INTERESES A CORTO PLAZO DE CREDITOS','N',0,'INTERESES A CORTO PLAZO DE CREDITOS',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Intereses a cobrar, con vencimiento no superior a un año, de créditos.\r\n\r\nFigurará en el activo corriente del balance.\r\n\r\nSu movimiento es el siguiente:\r\n\r\na) Se cargará por los intereses explícitos devengados, cuyo vencimiento no sea superior a un año, con abono     a la cuenta 762.\r\n\r\nb) Se abonará:\r\n    b1) Por el importe de los intereses cobrados, con cargo a cuentas del subgrupo 57.\r\n    b2) Por el reintegro, total o parcial, o baja del activo, con cargo, generalmente a cuentas del subgrupo 57 y           en caso de pérdidas a la cuenta 667.\r\n',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('548','IMPOSICIONES A CORTO PLAZO','N',0,'IMPOSICIONES A CORTO PLAZO',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Saldos favorables en Bancos e Instituciones de Crédito formalizados por medio de \"cuenta a plazo\" o similares, con vencimiento no superior a un año y de acuerdo con las condiciones que rigen para el sistema financiero. \r\n\r\nTambién se incluirán, con el debido desarrollo en cuentas de cuatro cifras, los intereses a cobrar, con vencimiento no superior a un año, de imposiciones a plazo. Cuando las imposiciones a plazo hayan sido concertadas con partes vinculadas, la inversión se reflejará en la cuenta 532. \r\n\r\nFigurará en el activo corriente del balance.\r\n\r\nSu movimiento es el siguiente: \r\n\r\na) Se cargará a la formalización, por el importe entregado. \r\n\r\nb) Se abonará a la recuperación o traspaso de los fondos. \r\n',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('549','DESEMBOLSOS PDTES. SOBRE PARTIC. PATRIMONIO NETO CORTO PLAZO','N',0,'DESEMBOLSOS PDTES. SOBRE PARTIC. PATRIMONIO NETO CORTO PLAZO',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Desembolsos pendientes, no exigidos, sobre participaciones en el patrimonio neto de empresas que no tengan la consideración de partes vinculadas, cuando se trate de inversiones financieras temporales. \r\n\r\nFigurará en el activo corriente del balance, minorando la partida en la que se contabilicen las correspondientes participaciones.\r\n\r\nSu movimiento es el siguiente: \r\n\r\na) Se abonará a la adquisición o suscripción de las acciones, por el importe pendiente de desembolsar, con         cargo a la cuenta 540. \r\n\r\nb) Se cargará por los desembolsos que se vayan exigiendo, con abono a la cuenta 556 o a la cuenta 540 por     lo saldos pendientes, cuando se enajenen acciones no desembolsadas totalmente. .\r\n',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
