insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('802','TRANSFERENCIA DE BENEFICIOS ACTIVOS FINANC. DISPONIBLE VENTA','N',0,'TRANSFERENCIA DE BENEFICIOS ACTIVOS FINANC. DISPONIBLE VENTA',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Su movimiento es el siguiente:\r\n\r\na) Se cargará\r\n    a1) En el momento en que se produzca la baja o enajenación del activo financiero disponible para la venta,           incluidos los que hayan sido objeto de reclasificación, por el saldo positivo acumulado en el patrimonio           neto con abono a la cuenta 7632.\r\n\r\na2) En caso de reclasificación en una inversión mantenida hasta el vencimiento, por el saldo positivo       acumulado en el patrimonio neto que se reconoce en la cuenta de pérdidas y ganancias, a lo largo de su       vida residual, como un incremento de los ingresos financieros en los términos establecidos en la norma de registro y valoración, con abono a la cuenta 761.\r\n\r\nb) Se abonará, al cierre del ejercicio, con cargo a la cuenta 133.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
