insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('810','PERDIDAS POR COBERTURAS DE FLUJOS DE EFECTIVO','N',0,'PERDIDAS POR COBERTURAS DE FLUJOS DE EFECTIVO',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Su movimiento es el siguiente:\r\n\r\na) Se cargará, por el menor valor de los siguientes importes: el resultado negativo acumulado del instrumento     de cobertura desde el inicio de la cobertura o el cambio acumulado en el valor razonable de los flujos de     efectivo futuros esperados de la partida cubierta desde el inicio de la cobertura; con abono, generalmente,     a las cuentas 175, 255 ó 559.\r\n\r\nb) Se abonará, al cierre del ejercicio, con cargo a la cuenta 1340.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('811','PERDIDAS POR COBERTURAS DE INVERS. NETAS EN EL EXTRANJERO','N',0,'PERDIDAS POR COBERTURAS DE INVERS. NETAS EN EL EXTRANJERO',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Su movimiento es el siguiente:\r\n\r\na) Se cargará por el importe de la cobertura que se determine eficaz, con abono, generalmente, a las cuentas 175, 255 ó 559.\r\n\r\nb) Se abonará, al cierre del ejercicio, con cargo a la cuenta 1341.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('812','TRANSF. DE BENEFICIOS POR COBERTURAS DE FLUJOS EFECTIVO','N',0,'TRANSF. DE BENEFICIOS POR COBERTURAS DE FLUJOS EFECTIVO',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'a) Se cargará:\r\n\r\na1) Cuando la cobertura de una transacción prevista o de cobertura del riesgo de tipo de cambio de un compromiso en firme, diera lugar al reconocimiento posterior de un activo financiero o pasivo financiero, por el importe positivo reconocido directamente en el patrimonio neto, a medida que dicho activo o pasivo afecte al\r\nresultado del ejercicio, con abono a la cuenta que corresponda en función de la naturaleza del gasto que genere la partida cubierta .\r\n\r\na2) Cuando la cobertura de una transacción prevista o de cobertura del riesgo de tipo de cambio de un compromiso en firme, diera lugar al reconocimiento de un activo o pasivo no financiero, por el importe positivo reconocido directamente en el patrimonio neto, con abono, a la cuenta del correspondiente elemento\r\npatrimonial.\r\n\r\na3) Cuando la cobertura de una transacción prevista o de cobertura del riesgo de tipo de cambio de un compromiso en firme, diera lugar a la baja de un activo o pasivo no financiero, por el importe positivo reconocido directamente en el patrimonio neto, con abono a la cuenta que corresponda a la naturaleza del gasto que genere la partida cubierta.\r\n\r\na4) Por el importe de la ganancia directamente reconocida en el patrimonio neto, si la empresa no espera que la transacción prevista tenga lugar, con abono a la cuenta 7633.\r\n\r\nb) Se abonará, al cierre del ejercicio, con cargo a la cuenta 1340.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('813','TRANSF. BENEFICIOS POR COBERTURAS INVERS. NETAS EXTRANJERO','N',0,'TRANSF. BENEFICIOS POR COBERTURAS INVERS. NETAS EXTRANJERO',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Su movimiento es el siguiente:\r\n\r\na) Se cargará, en el momento de la venta o disposición por otra vía de la inversión neta en un negocio en el extranjero, por el importe del beneficio del instrumento de cobertura imputado directamente al patrimonio neto, con abono a la cuenta 768.\r\n\r\nb) Se abonará, al cierre del ejercicio, con cargo a la cuenta 1341.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
