insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('8300','IMPUESTO CORRIENTE','N',0,'IMPUESTO CORRIENTE',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'a) Se cargará:\r\n    a1) Por la cuota a ingresar asociada a los ingresos imputados al patrimonio neto, con abono a la cuenta           4752.\r\n    a2) Por las retenciones soportadas y los ingresos a cuenta del impuesto realizados, asociados a los ingresos           imputados al patrimonio neto, hasta el importe de la cuota líquida del período, con abono a la cuenta           473.\r\n\r\nb) Se abonará, por la cuota de ejercicios anteriores que recupera la empresa como consecuencia de las liquidaciones fiscales del impuesto o impuestos sobre el beneficio, con cargo, a las cuenta 4709.\r\n\r\nc) Al cierre del ejercicio, se cargará o abonará, con abono o cargo a las correspondientes cuentas del subgrupo 13.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('8301','IMPUESTO DIFERIDO','N',0,'IMPUESTO DIFERIDO',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Su movimiento es el siguiente:\r\n\r\na) Se cargará:\r\n    a1) Por el impuesto diferido asociado a los ingresos reconocidos directamente en el patrimonio neto, con           abono a la cuenta 479.\r\n    a2) En el momento en que se produzca la transferencia a resultados del saldo negativo acumulado en el           patrimonio neto, con abono a la cuenta 4740.\r\n    a3) Por el importe del efecto impositivo derivado de la transferencia a resultados de gastos imputados          directamente al patrimonio neto que hubieran ocasionado el correspondiente impuesto corriente en                   ejercicios previos, con abono a la cuenta 6301.\r\n\r\nb) Se abonará:\r\n    b1) Por el impuesto diferido asociado a los gastos reconocidos directamente en el patrimonio neto, con           cargo a la cuenta 4740.\r\n    b2) En el momento en que se produzca la transferencia a resultados del saldo positivo acumulado en el           patrimonio neto, con cargo a la cuenta 479.\r\n    b3) Por el importe del efecto impositivo derivado de la transferencia a resultados de ingresos imputados                   directamente al patrimonio neto que hubieran ocasionado el correspondiente impuesto corriente en                   ejercicios previos, con cargo a la cuenta 6301.\r\n\r\nc) Al cierre del ejercicio, se cargará o abonará, con abono o cargo a las correspondientes\r\ncuentas del subgrupo 13.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('833','AJUSTES NEGATIVOS EN LA IMPOSICION SOBRE BENEFICIOS','N',0,'AJUSTES NEGATIVOS EN LA IMPOSICION SOBRE BENEFICIOS',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Disminución, conocida en el ejercicio, de los activos por impuesto diferido o aumento, igualmente conocido en el ejercicio, de los pasivos por impuesto diferido, respecto de los activos y pasivos por impuesto diferido anteriormente generados, siempre y cuando dichos saldos se hayan originado como consecuencia de una transacción o suceso que se hubiese reconocido directamente en una partida del patrimonio neto.\r\n\r\na) Se cargará:\r\n    a1) Por el menor importe del activo por diferencias temporarias deducibles, con abono a la cuenta 4740.\r\n    a2) Por el mayor importe del pasivo por diferencias temporarias imponibles, con abono a la cuenta 479.\r\n\r\nb) Al cierre del ejercicio, se abonará con cargo a las correspondientes cuentas del subgrupo 13.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('834','INGRESOS FISCALES POR DIFERENCIAS PERMANENTES','N',0,'INGRESOS FISCALES POR DIFERENCIAS PERMANENTES',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Su movimiento es el siguiente:\r\n\r\na) Se abonará, generalmente, con cargo a la cuenta 6301, por el importe del efecto impositivo de las diferencias permanentes a imputar en varios ejercicios.\r\n\r\nb) Al cierre del ejercicio, se cargará con abono a la cuenta 137.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
