insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('836','TRANSFERENCIA DE DIFERENCIAS PERMANENTES','N',0,'TRANSFERENCIA DE DIFERENCIAS PERMANENTES',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Su movimiento es el siguiente:\r\n\r\na) Se cargará, generalmente, con abono a la cuenta 6301, por la parte correspondiente a imputar en el ejercicio, de forma correlacionada con la depreciación del activo que motive la diferencia permanente.\r\n\r\nb) Al cierre del ejercicio, se abonará con cargo a la cuenta 137.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
