insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('912','TRANSFERENCIA DE PERDIDAS POR COBERTURAS DE FLUJO EFECTIVO','N',0,'TRANSFERENCIA DE PERDIDAS POR COBERTURAS DE FLUJO EFECTIVO',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Su movimiento es el siguiente:\r\n\r\na) Se abonará:\r\n\r\na1) Cuando la cobertura de una transacción prevista o de cobertura del riesgo de tipo de cambio de un       compromiso en firme, diera lugar al reconocimiento posterior de un activo o pasivo financiero, por el       importe negativo reconocido directamente en el patrimonio neto, a medida que dicho activo o pasivo       afecte al resultado del ejercicio, con cargo a la cuenta que corresponda a la naturaleza del ingreso que       genere la partida de cubierta.\r\na2) Cuando la cobertura de una transacción prevista o de cobertura del riesgo de tipo de cambio de un       compromiso en firme, diera lugar al reconocimiento de un activo o pasivo no financiero, por el importe       negativo reconocido directamente en el patrimonio neto, con cargo, a la cuenta del correspondiente       elemento patrimonial.\r\na3) Cuando la cobertura de una transacción prevista o de cobertura del riesgo de tipo de cambio de un       compromiso en firme, diera lugar a la baja de un activo o pasivo no financiero, por el importe negativo       reconocido directamente en el patrimonio neto, con cargo a la cuenta que corresponda a la naturaleza del       ingreso que genere la partida de cubierta.\r\na4) Cuando en la cobertura de un activo o un pasivo reconocido, la partida cubierta afecte al resultado, con cargo a una cuenta que se imputará a la cuenta de pérdidas y ganancias en la misma partida en la que se incluye la ganancia que se genere en la partida cubierta.\r\na5) Por el importe de la pérdida directamente reconocida en el patrimonio neto que la empresa no espere       recuperar, con cargo a la cuenta 6633. \r\n\r\nb) Se cargará, al cierre del ejercicio, con abono a la cuenta 1340.\r\n',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('913','TRANSFERENCIA PERDIDAS POR COBERTURAS INVERSION EXTRANJERO','N',0,'TRANSFERENCIA PERDIDAS POR COBERTURAS INVERSION EXTRANJERO',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Su movimiento es el siguiente:\r\n\r\na) Se abonará, en el momento de la venta o disposición por otra vía de la inversión neta en un negocio en el extranjero, por el importe de la pérdida del instrumento de cobertura imputada directamente al patrimonio neto, con cargo a la cuenta 668.\r\n\r\nb) Se cargará, al cierre del ejercicio, con abono a la cuenta 1341.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
