insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('960','BENEF.ACTIVOS NO CTES. Y GRUPOS ENAJ. ELEMNT. MANT. VENTA','N',0,'BENEF.ACTIVOS NO CTES. Y GRUPOS ENAJ. ELEMNT. MANT. VENTA',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Con caracter general, su movimiento es el siguiente:\r\n\r\na) Se abonará por las variaciones positivas en el valor razonable de los activos no corrientes mantenidos para la venta, y de activos y pasivos directamente asociados clasificados en un grupo enajenable de elementos mantenidos para la venta, que deban valorarse por el valor razonable con cambios en el patrimonio neto de acuerdo con lo dispuesto en las normas de registro y valoración, con cargo a cuentas del subgrupo 58.\r\n\r\nb) Se cargará, al cierre del ejercicio, con abono a la cuenta 136.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('962','TRANSF. PERDIDA ACTIVOS NO CTES. Y GRUPOS ENAJ. PARA LA VENT','N',0,'TRANSF. PERDIDA ACTIVOS NO CTES. Y GRUPOS ENAJ. PARA LA VENT',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Con caracter general, su movimiento es el siguiente:\r\n\r\na) Se abonará:\r\n   a1) En el momento en que se produzca la baja o enajenación del activo no corriente mantenido para la          venta, o del activo o pasivo directamente asociado clasificado en un grupo enajenable de elementos          mantenidos para la venta, que deba valorarse por el valor razonable con cambios en el patrimonio neto          de acuerdo con lo dispuesto en las normas de registro y valoración, con cargo, generalmente, a la          cuenta 6632.\r\n   a2) En el momento en que se produzca el deterioro del activo no corriente mantenido para la venta, o del          activo directamente asociado clasificado en un grupo enajenable de elementos mantenidos para la          venta, que deba valorarse por el valor razonable con cambios en el patrimonio neto de acuerdo con lo          dispuesto en las normas de registro y valoración, por el saldo negativo acumulado en el patrimonio neto,          con cargo a las cuentas de los correspondientes instrumentos de deuda o a la cuenta 698 en el caso de          inversiones en instrumentos de patrimonio.\r\n\r\nb) Se cargará, al cierre del ejercicio, con abono a la cuenta 136.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
insert  into `cuentas`(`codmacta`,`nommacta`,`apudirec`,`model347`,`razosoci`,`dirdatos`,`codposta`,`despobla`,`desprovi`,`nifdatos`,`maidatos`,`webdatos`,`obsdatos`,`numpoliz`,`fecsolic`,`credisol`,`fecconce`,`credicon`,`observa`,`fecbloq`,`forpa`,`ctabanco`,`grupotesoreria`,`fecbajcre`,`iban`,`SEPA_Refere`,`SEPA_FecFirma`,`codpais`,`codigiva`,`codcontrhab`,`esctamultiple`) values ('99','INGR. PARTIC.  PATRIMONIO EMPR. GRUPO CON AJUSTES VAL.NEGAT.','N',0,'INGR. PARTIC.  PATRIMONIO EMPR. GRUPO CON AJUSTES VAL.NEGAT.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,'Las cuentas de este subgrupo recogerán la recuperación de los ajustes valorativos por reducciones de valor imputados directamente en el patrimonio neto, cuando se hubieran realizado inversiones previas a la consideración de las participaciones en el patrimonio como de empresas del grupo, multigrupo y asociadas. También se recogerán las transferencias a la cuenta de pérdidas y ganancias de los citados ajustes valorativos en caso de deterioro. Todo ello, de acuerdo con lo que al respecto disponen las correspondientes normas de registro y valoración.',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,'ES',NULL,NULL,0);
